query:
// Hunt for DynoWiper (Sandworm/APT44) IOC
// SHA256: 835b0d87ed2d49899ab6f9479cddb8b4e03f5aeb2365c50a51f9088dcede68d5
let dynowiper_sha256 = '835b0d87ed2d49899ab6f9479cddb8b4e03f5aeb2365c50a51f9088dcede68d5';
let lookback = 30d;
// File presence on disk
let FileHits = DeviceFileEvents
| where TimeGenerated >= ago(lookback)
| where SHA256 == dynowiper_sha256
| extend HitType = 'FileOnDisk'
| project TimeGenerated, DeviceName, HitType, FileName, FolderPath, InitiatingProcessAccountName;
// Process execution
let ProcessHits = DeviceProcessEvents
| where TimeGenerated >= ago(lookback)
| where SHA256 == dynowiper_sha256
| extend HitType = 'ProcessExecution'
| project TimeGenerated, DeviceName, HitType, FileName, FolderPath, AccountName = AccountUpn, ProcessCommandLine;
// MDE Alerts
let AlertHits = DeviceAlertEvents
| where TimeGenerated >= ago(lookback)
| where SHA256 == dynowiper_sha256
| extend HitType = 'MDEAlert'
| project TimeGenerated, DeviceName, HitType, FileName, AlertId, Title, Severity;
FileHits
| project TimeGenerated, DeviceName, HitType, Details = strcat('File: ', FolderPath, '\\', FileName, ' | Account: ', InitiatingProcessAccountName)
| union (
    ProcessHits | project TimeGenerated, DeviceName, HitType, Details = strcat('Executed: ', FolderPath, '\\', FileName, ' | Cmd: ', ProcessCommandLine)
)
| union (
    AlertHits | project TimeGenerated, DeviceName, HitType, Details = strcat('Alert: ', Title, ' | Severity: ', Severity)
)
| sort by TimeGenerated desc
